int costone = 0;
for(int i=0;i&lt;chips.length;i++){
if(chips[i] % 2 == 0){
cost = cost + 1;
}else{
costone = costone + 1;
}
}
if(cost &gt; costone){
return costone;
}
return cost;
}
}
